/**
 * Get schema function.
 * @function getSchema
 * @param {string} type Content type.
 * @param {string} url Content url.
 * @returns {Object} Get schema action.
 */
export function getSchema(type: string, url: string): any;
/**
 * Post schema function.
 * @function postSchema
 * @param {string} type Content type.
 * @param {Object} data Schema data.
 * @returns {Object} Post schema action.
 */
export function postSchema(type: string, data: any): any;
/**
 * Put schema function.
 * @function putSchema
 * @param {string} type Content type.
 * @param {Object} data Schema data.
 * @returns {Object} Put schema action.
 */
export function putSchema(type: string, data: any): any;
/**
 * Update schema function.
 * @function updateSchema
 * @param {string} type Content type.
 * @param {Object} data Schema data.
 * @returns {Object} Update schema action.
 */
export function updateSchema(type: string, data: any): any;
